&#39;&#39;&#39; 全局设置和共用函数 &#39;&#39;&#39; import os from datetime import datetime from functools import wraps import ipaddress from openpyxl import load_workbook from multiprocessing.pool import ThreadPool from netmiko import ConnectHandler, NetMikoAuthenticationException, NetMikoTimeoutException, ssh_exception # 项目根目录 BASE_PATH = os.path.dirname((__file__)) # 文件输出目录 EXPORT_PATH = os.path.join(BASE_PATH, &#39;EXPORT&#39;) # 定义目录名称为当天日期（格式：20220609） dir_name = datetime.now().strftime(&#34;%Y%m%d&#34;) # 目录创建 new_path = os.path.join(EXPORT_PATH, dir_name) if not os.path.isdir(new_path): os.makedirs(new_path) backup_path = os.path.normpath( os.path.join(EXPORT_PATH, dir_name, &#39;config_backup&#39;)) config_path = os.path.normpath( os.path.join(EXPORT_PATH, dir_name, &#39;config_add&#39;)) # generate_table = os.path.normpath( # os.path.join(EXPORT_PATH, dir_name, &#39;generate_table&#39;)) if not os.path.isdir(backup_path): os.makedirs(backup_path) if not os.path.isdir(config_path): os.makedirs(config_path) # if not os.path.isdir(generate_table): # os.makedirs(generate_table) # 表格数据文件路径 device_file = &#34;dev_data.xlsx&#34; # ThreadPool 设定异步进程数为66 t_pool = ThreadPool(66) # 定义列表保存执行成功和失败的主机IP sucessful_list = [] failed_list = [] # 手动输入登陆信息 def login_start(): # login module，校验由netmiko完成 # login_user = input(&#39;Login:&#39;) # login_pwd = getpass.getpass(&#39;Passwd:&#39;) # # 明文写死在代码，不安全的方式 # login_user = &#39;admin&#39; # login_pwd = &#39;xxx&#39; # return login_user, login_pwd pass # 加载excel文件 def load_excel(): try: wb = load_workbook(device_file) return wb except FileNotFoundError: print(&#34;{} excel文件不存在&#34;.format(device_file)) except Exception: print(&#34;载入读取{} excel文件失败&#34;.format(device_file)) # 获取设备数据信息 def get_device_info(task_name): try: # by openpyxl # user, pwd = login_start() wb = load_excel() ws1 = wb[wb.sheetnames[0]] # 选定单元格数据区域 for row in ws1.iter_rows(min_row=2, max_col=9): # 判断IP所在的列不为空值，则进行如下代码 if row[2].value: if str(row[1].value).strip() == &#39;#&#39;: continue info_dict = { &#39;ip&#39;: row[2].value, &#39;username&#39;: row[5].value, &#39;password&#39;: row[6].value, &#39;protocol&#39;: row[3].value, &#39;port&#39;: row[4].value, &#39;secret&#39;: row[7].value, &#39;device_type&#39;: row[8].value, &#39;cmd_list&#39;: get_cmd_info(task_name, wb[row[8].value.strip().lower()]), } yield info_dict else: break except Exception as e: print(&#34;get_device_info failed: {}&#34;.format(e)) finally: wb.close() # 获取命令信息 def get_cmd_info(task_name, sheet_name): cmd_list = [] try: # by openpyxl for row in sheet_name.iter_rows(min_row=2, max_col=3): # 若单元格使用“＃”进行注释或命令为空值，则跳过该行 if str(row[0].value).strip() != &#39;#&#39; and row[1].value and task_name == &#39;backup_config.py&#39;: cmd_list.append(row[1].value.strip()) elif str(row[0].value).strip() != &#39;#&#39; and row[2].value and task_name == &#39;add_config.py&#39;: cmd_list.append(row[2].value.strip()) return cmd_list except Exception as e: print(&#34;get_cmd_info Error: &#34;, e) # 获取自定义设备数据信息 def get_undifined_device_info(): try: # by openpyxl wb = load_excel() ws1 = wb[wb.sheetnames[5]] row_number = 2 # 选定单元格数据区域 for row in ws1.iter_rows(min_row=2, max_col=9): # 获取执行命令 undifined_cmd_info = [] for cols in ws1.iter_cols(min_col=10, min_row=row_number, max_row=row_number, values_only=True): for col in cols: if col is None: continue undifined_cmd_info.append(col) row_number += 1 # 判断IP所在的列不为空值，执行如下代码 if row[2].value: if str(row[1].value).strip() == &#39;#&#39;: continue info_dict = { &#39;ip&#39;: row[2].value, &#39;username&#39;: row[5].value, &#39;password&#39;: row[6].value, &#39;protocol&#39;: row[3].value, &#39;port&#39;: row[4].value, &#39;secret&#39;: row[7].value, &#39;device_type&#39;: row[8].value, &#39;cmd_list&#39;: undifined_cmd_info, } yield info_dict else: break except Exception as e: print(&#34;get_device_info failed: {}&#34;.format(e)) finally: wb.close() # netmiko 连接处理 def connect_handler(host): try: connect = &#39;&#39; # 判断使用SSH协议 # 将“protocol”列单元格的内容大写转小写，去除前后空格对比是否为 ssh if host[&#39;protocol&#39;].lower().strip() == &#39;ssh&#39;: # 判断“port”列单元格，若单元格填写的内容不是22或空，则定义为22 host[&#39;port&#39;] = host[&#39;port&#39;] if (host[&#39;port&#39;] not in [22, None]) else 22 # 剔除多余connectHandler 不需要的参数，protocol、secret、cmd_list，华为华为设备无特权密码 host.pop(&#39;protocol&#39;), host.pop(&#39;cmd_list&#39;) if &#39;huawei&#39; in host[&#39;device_type&#39;]: host.pop(&#39;secret&#39;) connect = ConnectHandler(**host, conn_timeout=10) elif &#39;hp_comware&#39; in host[&#39;device_type&#39;]: host.pop(&#39;secret&#39;) connect = ConnectHandler(**host) else: connect = ConnectHandler(**host, conn_timeout=10) # 判断使用Telnet协议 elif host[&#39;protocol&#39;].lower().strip() == &#39;telnet&#39;: # 判断“port”列单元格，若单元格填写的内容不是23或空，则定义为23 host[&#39;port&#39;] = host[&#39;port&#39;] if (host[&#39;port&#39;] not in [23, None]) else 23 # 剔除多余connectHandler 不需要的参数，protocol、secret、cmd_list，华三华为设备无特权密码 host.pop(&#39;protocol&#39;), host.pop(&#39;secret&#39;), host.pop(&#39;cmd_list&#39;) # netmiko 支持telnet协议，设备类型格式为：hp_comware_telnet host[&#39;device_type&#39;] = host[&#39;device_type&#39;] + &#39;_telnet&#39; # fast_cli=false,待测试参数 connect = ConnectHandler(**host, fast_cli=False) # 不在以上两种协议内的连接 else: res = &#34;暂不支持IP地址为{}_的设备使用{}协议登陆&#34;.format(host[&#39;ip&#39;], host[&#39;protocol&#39;]) raise ValueError(res) return True, connect except NetMikoTimeoutException: res = &#34;{} Can not connect to Device!&#34;.format(host[&#39;ip&#39;]) print(res) return False, res except NetMikoAuthenticationException: res = &#34;{} username/password wrong!&#34;.format(host[&#39;ip&#39;]) print(res) return False, res except ssh_exception: res = &#34;{} SSH parameter problem!&#34;.format(host[&#39;ip&#39;]) print(res) return False, res except Exception as e: print(&#34;{} Failed: {}&#34;.format(host[&#39;ip&#39;], e)) return False, res # 记录程序执行时间装饰器 def timer(func): @wraps(func) def wrapper(*args, **kwargs): start_time = datetime.now() func(*args, **kwargs) end_time = datetime.now() # print(&#39;
&#39; + &#39;-&#39; * 42) print(&#39;执行完毕，共耗时 {:0.2f} 秒.&#39;.format((end_time - start_time).total_seconds())) print(&#39;-&#39; * 42) # return res return wrapper # 记录运行结果的装饰器 def result_count(func): @wraps(func) def wrapper(*args, **kwargs): task_name, sucessful_list, failed_list = func(*args, **kwargs) result_count = (&#39;设备总数 {} 台，成功 {} 台，失败 {} 台.&#39;.format( len(sucessful_list) + len(failed_list), len(sucessful_list), len(failed_list))) print(&#39;
